mapper.insert:
insert into bl_user(
 username, password, name, sex, header, nickname, user_email
) values (
#{username},#{password},#{name},#{sex},#{header},#{nickname},#{userEmail}
)

mapper.update:
update bl_user set version = version + 1

    ,username = #{username}


    ,password = #{password}


    ,name = #{name}


    ,sex = #{sex}


    ,header = #{header}


    ,nickname = #{nickname}


    ,user_email = #{userEmail}

where user_id = #{userId}
and version = #{version}

mapper.select:
select
user_id, username, name, sex, header, nickname, user_email, created_time, update_time
from bl_user
where
deleted = 0

    and username = #{params.username}


    and name like CONCAT('%', #{params.name}, '%')


    and sex = #{params.sex}


    and nickname like CONCAT('%', #{params.nickname}, '%')


    and user_email like CONCAT(#{params.userEmail}, '%')


    order by ${sortColumn} ${sortMethod}

limit #{index}, #{pageSize}

select count(*) from bl_user where deleted = 0
and username = #{params.username}
and name like CONCAT('%', #{params.name}, '%')
and sex = #{params.sex}
and nickname like CONCAT('%', #{params.nickname}, '%')
and user_email like CONCAT(#{params.userEmail}, '%')
